main.script:
            
                function update_records(is_available=false, is_location=false, is_language=false, is_speciality=false)
                {
                    var available_obj = document.getElementById('is_available_id');
                    var location_obj = document.getElementById('is_location_id');
                    var language_obj = document.getElementById('is_language_id');
                    var speciality_obj = document.getElementById('is_speciality_id');

                    if (!is_available && available_obj.style.backgroundColor != 'black'){
                        is_available = 1;
                    }

                    if (!is_location && location_obj.style.backgroundColor != 'black'){
                        is_location = location_obj.textContent;
                    }

                    if (!is_language && language_obj.style.backgroundColor != 'black'){
                        is_language = language_obj.textContent;
                    }

                    if (!is_speciality && speciality_obj.style.backgroundColor != 'black'){
                        is_speciality = speciality_obj.textContent;
                    }


                    if (is_available || is_location || is_language || is_speciality)
                    {
                        document.getElementById('see_less_div').style.display = 'none';
                        document.getElementById('see_lese_more_buttons_div').style.display = 'none';

                        $.ajax({
                          url: "/filter/providers",
                          type: "get",
                          data: {
                            'availability': is_available,
                            'location': is_location,
                            'language': is_language,
                            'speciality': is_speciality,
                          },
                          success: function(response) {
                            res = JSON.parse(response);

                            p_div = document.getElementById('filtered_div');
                            p_div.innerHTML = " ";

                            for (let i = 0; i < res['providers_count']; i++)
                            {
                                p_div.innerHTML += `<div
                                                              class="col-lg-3 col-md-6 icon-box"
                                                              data-aos="zoom-in"
                                                              data-aos-delay="1"
                                                              style="background-color: #f4f4f4; padding: 40px 10px; cursor: pointer;"
                                                              data-partner-id="${res['providers_data'][i]['partner_id']}"
                                                              onclick="handleCardClick(this);">

                                                                <div class="icon" style="width: 110px; height: 110px">
                                                                  <img style="width: 110px; height: 110px" src="${res['providers_data'][i]['thumbnail_img']}" />
                                                                </div>
                                                                <h4 class="title"><a href="/provider?partner_id=${res['providers_data'][i]['partner_id']}">${res['providers_data'][i]['name']}</a></h4>
                                                                <div style="line-height: 5px; padding: 10px 14px">
                                                              <div class="d-flex gap-1">
                                                                <h6 style="font-weight: 700; font-size: 14px;height: 49px;">${res['providers_data'][i]['license_type']} <br/>#${res['providers_data'][i]['license_number']}</h6>
                                                              </div>
                                                              <div class="d-flex gap-1">
                                                                <h6 style="font-weight: 700; font-size: 14px">
                                                                  Supervised By:
                                                                </h6>
                                                                <p style="margin-bottom: 0; margin-top: 7px; font-size: 12px;font-weight: 600;">
                                                                  ${res['providers_data'][i]['supervisor_name']}, ${res['providers_data'][i]['supervisor_license_type']} #${res['providers_data'][i]['supervisor_license_no']}
                                                                </p>
                                                              </div>
                                                              <div class="d-flex gap-1">
                                                                <h6 style="font-weight: 700; font-size: 14px">Location:</h6>
                                                                <p style="margin-bottom: 0; margin-top: 7px; font-size: 12px;font-weight: 600;">
                                                                  ${res['providers_data'][i]['location']}
                                                                </p>
                                                              </div>
                                                              <div class="d-flex gap-1">
                                                                <h6 style="font-weight: 700; font-size: 14px">Rates:</h6>
                                                                <p style="margin-bottom: 0; margin-top: 7px; font-size: 12px;font-weight: 600;">
                                                                  ${res['providers_data'][i]['rates']}
                                                                </p>
                                                              </div>
                                                              <div class="d-flex gap-1">
                                                                <h6 style="font-weight: 700; font-size: 14px">Bio:</h6>
                                                                <h6 style="margin-bottom: 0;margin-top: 3px;font-size: 12px;font-weight: 500;overflow: hidden;text-overflow: ellipsis;text-align: left;height: 49px;">
                                                                  ${res['providers_data'][i]['bio_preview']}
                                                                </h6>
                                                              </div>
                                                            </div>

                                                <div
                                                  class="text-center"
                                                  style="
                                                    background-color: #d85e31;
                                                    padding: 10px 15px;
                                                    margin: 0 20px;
                                                  "
                                                >
                                                  <a href="#" style="color: white;font-family: serif;" onclick="event.stopPropagation(); cl(this)">Book Appointment</a>
                                                </div>
                                              </div>`
                             }
                          },
                          error: function(xhr) {
                            console.error("Got error");
                            console.error(xhr);
                          }
                        });
                    }
                    else
                    {
                        p_div = document.getElementById('filtered_div');
                        p_div.innerHTML = " ";
                        document.getElementById('see_less_div').style.display = 'flex';
                        document.getElementById('see_lese_more_buttons_div').style.display = 'block';
                    }
                }

                function availability_filters(obj)
                {
                    if(obj.style.backgroundColor == 'black')
                    {
                        obj.style.backgroundColor = '#d85e31';
                        update_records(1);
                    }
                    else
                    {
                        obj.style.backgroundColor = 'black';
                        update_records();
                    }
                }

                function LocationFilter(option) {
                    const button = document.querySelector('.dropdown-btn');
                    if (!button.textContent.includes(option))
                    {
                        button.textContent = `${option}`;
                        button.style.backgroundColor = '#d85e31';

                        update_records(0, option);
                    }
                    else
                    {
                        button.textContent = `Location`;
                        button.style.backgroundColor = 'black';
                        update_records();
                    }


                    const dropdown = document.querySelector('.dropdown');
                    dropdown.classList.remove('active');
                }

                function selectLangOption(obj){
                    var button = document.querySelector('.dropdown-lang-btn');

                    if (!button.textContent.includes(obj.textContent))
                    {
                        button.textContent = `${obj.textContent}`;
                        button.style.backgroundColor = '#d85e31';
                        update_records(false, false, obj.textContent);
                    }
                    else
                    {
                        button.textContent = `Language`;
                        button.style.backgroundColor = 'black';
                        update_records();
                    }

                    const dropdown = document.querySelector('.lang_dropdown');
                    dropdown.classList.remove('active');
                }


                function selectSpecialityOption(obj)
                {
                    const button = document.querySelector('.dropdown-speciality-btn');
                    if (!button.textContent.includes(obj.textContent))
                    {
                        button.textContent = `${obj.textContent}`;
                        button.style.backgroundColor = '#d85e31';
                        update_records(false, false, false, obj.textContent);
                    }
                    else
                    {
                        button.textContent = `Speciality`;
                        button.style.backgroundColor = 'black';
                        update_records();
                    }


                    const dropdown = document.querySelector('.speciality_dropdown');
                    dropdown.classList.remove('active');
                }

div.script:
function toggleDropdown() {
    const dropdown = document.querySelector('.dropdown');
    dropdown.classList.toggle('active');
}
window.addEventListener('click', function (event) {
    const dropdown = document.querySelector('.dropdown');
    if (!dropdown.contains(event.target)) {
        dropdown.classList.remove('active');
    }
});
document.querySelector('.dropdown-content').addEventListener('click', function (event) {
    event.stopPropagation();
});

function toggleLangDropdown() {
    const dropdown = document.querySelector('.lang_dropdown');
    dropdown.classList.toggle('active');
}
window.addEventListener('click', function (event) {
    const dropdown = document.querySelector('.lang_dropdown');
    if (!dropdown.contains(event.target)) {
        dropdown.classList.remove('active');
    }
});
document.querySelector('.dropdown-lang-content').addEventListener('click', function (event) {
    event.stopPropagation();
});

function toggleSpecialityDropdown() {
    const dropdown = document.querySelector('.speciality_dropdown');
    dropdown.classList.toggle('active');
}
window.addEventListener('click', function (event) {
    const dropdown = document.querySelector('.speciality_dropdown');
    if (!dropdown.contains(event.target)) {
        dropdown.classList.remove('active');
    }
});
document.querySelector('.dropdown-speciality-content').addEventListener('click', function (event) {
    event.stopPropagation();
});

main.script:
          function cl(clicked_div) {
                document.getElementById('popup').style.display = 'block';

                var selectElement = document.getElementById("providr");
                var iconBox = clicked_div.closest('.icon-box');
                var h4Element = iconBox.querySelector('h4.title');
                var providerName = h4Element.textContent.trim();

                for (var i = selectElement.options.length - 1; i > 0; i--) {
                    if (selectElement.options[i].value === providerName) {
                        selectElement.selectedIndex = i;
                        break;
                    }                };
          };

         function closee() {
  var form = document.getElementById('myForm');
  if (form) {
    form.reset();
  }
  document.getElementById('popup').style.display = 'none';
}

