              function submitAppointmentForm2(event) {
                  event.preventDefault();
                const formData = new FormData()
                let name = document.getElementById('fname').value;
                let number = document.getElementById('fnumber').value;
                let email = document.getElementById('femail').value;
                let doctor = document.getElementById('fdoctor').value;

                var regex = /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,7}\b/;

                if (regex.test(email))
                {
                  if (name && number && doctor)
                  {
                    document.getElementById('femailError').innerHTML = '';

                    formData.append('name', name)
                    formData.append('number', number)
                    formData.append('email', email)
                    formData.append('interested_in', doctor)
                    formData.append('provider', document.getElementById('fprovidr').value)
                    formData.append('inPersonCheckbox', document.getElementById('finPersonCheckbox').checked)
                    formData.append('telehealthCheckbox', document.getElementById('ftelehealthCheckbox').checked)
                    formData.append('preferenceCheckbox', document.getElementById('fpreferenceCheckbox').checked)

                    $.ajax({
                      type: "POST",
                      url: '/create/lead',
                      contentType: false,
                      processData:false,
                      data: formData,
                    });
                    setTimeout(() => {
                      window.location.href='/thank-you';
                    }, 250)
                  }
                }
                else{
                    document.getElementById('femailError').innerHTML = 'Please enter a valid email address';
                    return false;
                }

              }
